00454 /********************************************************************************************************************/
00455 int8_t ADC_Init(ADC_Config *config)
00456 {
00457     if(config->Port == ADC_Configuration.Port._ADC1_) RCC -> APB2ENR |= RCC_APB2ENR_ADC1EN;
00458     else if(config->Port == ADC_Configuration.Port._ADC2_) RCC -> APB2ENR |= RCC_APB2ENR_ADC2EN;
00459     else if(config->Port == ADC_Configuration.Port._ADC3_)RCC -> APB2ENR |= RCC_APB2ENR_ADC3EN;
00460     else return -1;
00461 
00462     config -> Port -> CR1 |= ADC_CR1_SCAN ;
00463 
00464     if(config->Resolution == ADC_Configuration.Resolution.Bit_12) config -> Port -> CR1 &= ~ADC_CR1_RES;
00465     else if(config->Resolution == ADC_Configuration.Resolution.Bit_10) config -> Port -> CR1 |=  1 << ADC_CR1_RES_Pos;
00466     else if(config->Resolution == ADC_Configuration.Resolution.Bit_8)  config -> Port -> CR1 |=  2 << ADC_CR1_RES_Pos;
00467     else if(config->Resolution == ADC_Configuration.Resolution.Bit_6)  config -> Port -> CR1 |=  3 << ADC_CR1_RES_Pos;
00468     else return -1;
00469 
00470 
00471     if(config -> Conversion_Mode == ADC_Configuration.Conversion_Mode.Single){
00472         config -> Port -> CR2  &= ~ADC_CR2_CONT;
00473     }
00474     else if(config -> Conversion_Mode == ADC_Configuration.Conversion_Mode.Continuous){
00475         config -> Port -> CR2  |= ADC_CR2_CONT;
00476     }
00477     else {return -1;}
00478 
00479 
00480     config -> Port -> CR2 |= ADC_CR2_EOCS ;
00481 
00482     // Data Alignment
00483     if(config -> Data_Alignment == ADC_Configuration.Data_Alignment.Right_Justified)
00484     {
00485         config -> Port -> CR2 &= ~ADC_CR2_ALIGN;
00486     }
00487     else if(config -> Data_Alignment == ADC_Configuration.Data_Alignment.Left_Justified)
00488     {
00489         config -> Port -> CR2 |= ADC_CR2_ALIGN;
00490     }
00491     else {return -1;}
00492 
00493     pin_temp = ADC_Sampling_Config(config);
00494 
00495     config -> Port -> SQR1 |= (pin_temp-1) << ADC_SQR1_L_Pos;
00496 
00497     ADC_Pin_Init(config);
00498 
00499 
00500 
00501     if(config->Channel_Type == ADC_Configuration.Channel_Type.Regular)
00502     {
00503         config -> Port -> CR2 &= ~ADC_CR2_EXTSEL;
00504 
00505         if(config -> External_Trigger.Enable == ENABLE)
00506         {
00507 //          config -> Port -> CR2 |= config -> External_Trigger.Trigger_Event << ADC_CR2_EXTSEL_Pos;
00508             config -> Port -> CR2 |= ADC_CR2_EXTSEL_2 | ADC_CR2_EXTSEL_1;
00509             config -> Port -> CR2 |= ADC_CR2_EXTEN_0;
00510             ADC_Timer_External_Trigger_Init(config);
00511         }
00512     }
00513     else if(config->Channel_Type == ADC_Configuration.Channel_Type.Injected)
00514     {
00515         config -> Port -> CR2 &= ~ADC_CR2_JEXTSEL;
00516         config -> Port -> CR2 |= config -> External_Trigger.Enable << ADC_CR2_JEXTEN_Pos;
00517         config -> Port -> CR2 |= config -> External_Trigger.Trigger_Event << ADC_CR2_JEXTSEL_Pos;
00518         ADC_Timer_External_Trigger_Init(config);
00519 
00520     }
00521     else return -1;
00522 
00523 
00524     config -> Port -> CR2 |= ADC_CR2_DMA;
00525     config -> Port -> CR2 |= ADC_CR2_DDS;
00526 
00527     ADC_Sequence_Config(config);
00528 
00529     ADC_Enable(config);
00530 
00531     xADC.Request = DMA_Configuration.Request._ADC1;
00532     xADC.transfer_direction = DMA_Configuration.Transfer_Direction.Peripheral_to_memory;
00533     xADC.circular_mode = DMA_Configuration.Circular_Mode.Enable;
00534     xADC.flow_control = DMA_Configuration.Flow_Control.DMA_Control;
00535     xADC.memory_data_size = DMA_Configuration.Memory_Data_Size.half_word;
00536     xADC.peripheral_data_size = DMA_Configuration.Peripheral_Data_Size.half_word;
00537     xADC.memory_pointer_increment = DMA_Configuration.Memory_Pointer_Increment.Enable;
00538     xADC.peripheral_pointer_increment = DMA_Configuration.Peripheral_Pointer_Increment.Disable;
00539     DMA_Init(&xADC);
00540 
00541 
00542     return 1;
00543 }
00544 /********************************************************************************************************************/
00545 
00546 int8_t ADC_Enable(ADC_Config *config)
00547 {
00548     config -> Port -> CR2 |= ADC_CR2_ADON;
00549     Delay_milli(1000);
00550 //  uint32_t delay = 10000;
00551 //  while (delay--);
00552     return 1;
00553 }
00554 
00555 int8_t ADC_Start(ADC_Config *config)
00556 {
00557     config -> Port -> SR = 0;
00558     if(config->Channel_Type == ADC_Configuration.Channel_Type.Regular) {
00559         config -> Port -> CR2 |= ADC_CR2_SWSTART;
00560     }
00561     else if(config->Channel_Type == ADC_Configuration.Channel_Type.Injected){
00562         config -> Port -> CR2 |= ADC_CR2_JSWSTART;
00563     }
00564     else return -1;
00565     return 1;
00566 }
00567 
00568 int8_t ADC_Start_Capture(ADC_Config *config, uint16_t *buffer)
00569 {
00570     if ((config->Port->SR) &(ADC_SR_OVR))
00571     {
00572         config->Port->CR2 &= ~ADC_CR2_ADON;
00573         config->Port->CR2 |= ADC_CR2_ADON;
00574     }
00575 
00576     xADC.buffer_length = pin_temp;
00577     xADC.peripheral_address = (uint32_t)&(config->Port->DR);
00578     xADC.memory_address = (uint32_t)buffer;
00579 
00580     DMA_Set_Target(&xADC);
00581     DMA_Set_Trigger(&xADC);
00582     config -> Port -> SR = 0;
00583     ADC_Start(config);
00584     return 1;
00585 }
