00014 void USART_Config_Reset(USART_Config *config)
00015 {
00016     config->mode = USART_Configuration.Mode.Disable;
00017     config->hardware_flow = USART_Configuration.Hardware_Flow.Disable;
00018     config->baudrate = 9600;
00019     config->dma_enable = USART_Configuration.DMA_Enable.RX_Disable | USART_Configuration.DMA_Enable.TX_Disable;
00020     config->interrupt = USART_Configuration.Interrupt_Type.Disable;
00021 }
00022 
00023 
00024 int8_t USART_Clock_Enable(USART_Config *config)
00025 {
00026     if(config->Port == USART1)
00027     {
00028         RCC -> APB2ENR |= RCC_APB2ENR_USART1EN;
00029     }
00030     else if(config->Port == USART2)
00031     {
00032         RCC -> APB1ENR |= RCC_APB1ENR_USART2EN;
00033     }
00034     else if(config->Port == USART3)
00035     {
00036         RCC -> APB1ENR |= RCC_APB1ENR_USART3EN;
00037     }
00038     else if(config->Port == UART4)
00039     {
00040         RCC -> APB1ENR |= RCC_APB1ENR_UART4EN;
00041     }
00042     else if(config->Port == UART5)
00043     {
00044         RCC -> APB1ENR |= RCC_APB1ENR_UART5EN;
00045     }
00046     else if(config->Port == USART6)
00047     {
00048         RCC -> APB2ENR |= RCC_APB2ENR_USART6EN;
00049     }
00050     else
00051     {
00052         return -1;
00053     }
00054     return 1;
00055 }
00056 
00057 
00058 int8_t USART_Clock_Disable(USART_Config *config)
00059 {
00060     if(config->Port == USART1)
00061     {
00062         RCC -> APB2ENR &= ~RCC_APB2ENR_USART1EN;
00063     }
00064     else if(config->Port == USART2)
00065     {
00066         RCC -> APB1ENR &= ~RCC_APB1ENR_USART2EN;
00067     }
00068     else if(config->Port == USART3)
00069     {
00070         RCC -> APB1ENR &= ~RCC_APB1ENR_USART3EN;
00071     }
00072     else if(config->Port == UART4)
00073     {
00074         RCC -> APB1ENR &= ~RCC_APB1ENR_UART4EN;
00075     }
00076     else if(config->Port == UART5)
00077     {
00078         RCC -> APB1ENR &= ~RCC_APB1ENR_UART5EN;
00079     }
00080     else if(config->Port == USART6)
00081     {
00082         RCC -> APB2ENR &= ~RCC_APB2ENR_USART6EN;
00083     }
00084     else
00085     {
00086         return -1;
00087     }
00088     return 1;
00089 }
