00296 int8_t USART_Init(USART_Config *config)
00297 {
00298     USART_Clock_Enable(config);
00299     PIN_Setup(config);
00300 
00301 //  USART1 -> CR1 |= USART_CR1_UE;
00302 
00303     double brr = (168000000.0/ (16.0 * 2.0 * (double)(config->baudrate)));
00304     double div_frac, mantissa;
00305     separateFractionAndIntegral(brr, &div_frac, &mantissa);
00306 
00307     int div_frac_1 = (int)(ceil(div_frac*16.0));
00308     int mantissa_1 = (int)(ceil(mantissa));
00309 
00310     config->Port-> CR1 |= USART_CR1_UE;
00311     config->Port->BRR = (mantissa_1<<4)|(div_frac_1);
00312     config->Port->CR1 |= config->parity; //Parity
00313     config->Port->CR1 |= config->interrupt; //interrupt
00314     config->Port->CR2 |= config->stop_bits;
00315 
00316     if(config->mode == USART_Configuration.Mode.Single_Wire_Half_Duplex) config -> Port -> CR3 |= USART_CR3_HDSEL;
00317     if(config->mode == USART_Configuration.Mode.LIN) config -> Port -> CR2 |= USART_CR2_LINEN;
00318 
00319 
00320     config->Port->CR1 |= USART_CR1_RE | USART_CR1_TE  ;
00321     config->Port-> CR1 |= USART_CR1_UE;
00322 
00323     return 1;
00324 }
