00041 /* Cache Size ID Register Macros */
00042 #define CCSIDR_WAYS(x)         (((x) & SCB_CCSIDR_ASSOCIATIVITY_Msk) >> SCB_CCSIDR_ASSOCIATIVITY_Pos)
00043 #define CCSIDR_SETS(x)         (((x) & SCB_CCSIDR_NUMSETS_Msk      ) >> SCB_CCSIDR_NUMSETS_Pos      )
00044 
00045 #ifndef __SCB_DCACHE_LINE_SIZE
00046 #define __SCB_DCACHE_LINE_SIZE  32U 
00047 #endif
00048 
00049 #ifndef __SCB_ICACHE_LINE_SIZE
00050 #define __SCB_ICACHE_LINE_SIZE  32U 
00051 #endif
00052 
00057 __STATIC_FORCEINLINE void SCB_EnableICache (void)
00058 {
00059   #if defined (__ICACHE_PRESENT) && (__ICACHE_PRESENT == 1U)
00060     if (SCB->CCR & SCB_CCR_IC_Msk) return;  /* return if ICache is already enabled */
00061 
00062     __DSB();
00063     __ISB();
00064     SCB->ICIALLU = 0UL;                     /* invalidate I-Cache */
00065     __DSB();
00066     __ISB();
00067     SCB->CCR |=  (uint32_t)SCB_CCR_IC_Msk;  /* enable I-Cache */
00068     __DSB();
00069     __ISB();
00070   #endif
00071 }
00072 
00073 
00078 __STATIC_FORCEINLINE void SCB_DisableICache (void)
00079 {
00080   #if defined (__ICACHE_PRESENT) && (__ICACHE_PRESENT == 1U)
00081     __DSB();
00082     __ISB();
00083     SCB->CCR &= ~(uint32_t)SCB_CCR_IC_Msk;  /* disable I-Cache */
00084     SCB->ICIALLU = 0UL;                     /* invalidate I-Cache */
00085     __DSB();
00086     __ISB();
00087   #endif
00088 }
00089 
00090 
00095 __STATIC_FORCEINLINE void SCB_InvalidateICache (void)
00096 {
00097   #if defined (__ICACHE_PRESENT) && (__ICACHE_PRESENT == 1U)
00098     __DSB();
00099     __ISB();
00100     SCB->ICIALLU = 0UL;
00101     __DSB();
00102     __ISB();
00103   #endif
00104 }
