00115 __STATIC_FORCEINLINE void SCB_InvalidateICache_by_Addr (volatile void *addr, int32_t isize)
00116 {
00117   #if defined (__ICACHE_PRESENT) && (__ICACHE_PRESENT == 1U)
00118     if ( isize > 0 ) {
00119        int32_t op_size = isize + (((uint32_t)addr) & (__SCB_ICACHE_LINE_SIZE - 1U));
00120       uint32_t op_addr = (uint32_t)addr /* & ~(__SCB_ICACHE_LINE_SIZE - 1U) */;
00121 
00122       __DSB();
00123 
00124       do {
00125         SCB->ICIMVAU = op_addr;             /* register accepts only 32byte aligned values, only bits 31..5 are valid */
00126         op_addr += __SCB_ICACHE_LINE_SIZE;
00127         op_size -= __SCB_ICACHE_LINE_SIZE;
00128       } while ( op_size > 0 );
00129 
00130       __DSB();
00131       __ISB();
00132     }
00133   #endif
00134 }
