00141 __STATIC_FORCEINLINE void SCB_EnableDCache (void)
00142 {
00143   #if defined (__DCACHE_PRESENT) && (__DCACHE_PRESENT == 1U)
00144     uint32_t ccsidr;
00145     uint32_t sets;
00146     uint32_t ways;
00147 
00148     if (SCB->CCR & SCB_CCR_DC_Msk) return;  /* return if DCache is already enabled */
00149 
00150     SCB->CSSELR = 0U;                       /* select Level 1 data cache */
00151     __DSB();
00152 
00153     ccsidr = SCB->CCSIDR;
00154 
00155                                             /* invalidate D-Cache */
00156     sets = (uint32_t)(CCSIDR_SETS(ccsidr));
00157     do {
00158       ways = (uint32_t)(CCSIDR_WAYS(ccsidr));
00159       do {
00160         SCB->DCISW = (((sets << SCB_DCISW_SET_Pos) & SCB_DCISW_SET_Msk) |
00161                       ((ways << SCB_DCISW_WAY_Pos) & SCB_DCISW_WAY_Msk)  );
00162         #if defined ( __CC_ARM )
00163           __schedule_barrier();
00164         #endif
00165       } while (ways-- != 0U);
00166     } while(sets-- != 0U);
00167     __DSB();
00168 
00169     SCB->CCR |=  (uint32_t)SCB_CCR_DC_Msk;  /* enable D-Cache */
00170 
00171     __DSB();
00172     __ISB();
00173   #endif
00174 }
00175 
00176 
00181 __STATIC_FORCEINLINE void SCB_DisableDCache (void)
00182 {
00183   #if defined (__DCACHE_PRESENT) && (__DCACHE_PRESENT == 1U)
00184     uint32_t ccsidr;
00185     uint32_t sets;
00186     uint32_t ways;
00187 
00188     SCB->CSSELR = 0U;                       /* select Level 1 data cache */
00189     __DSB();
00190 
00191     SCB->CCR &= ~(uint32_t)SCB_CCR_DC_Msk;  /* disable D-Cache */
00192     __DSB();
00193 
00194     ccsidr = SCB->CCSIDR;
00195 
00196                                             /* clean & invalidate D-Cache */
00197     sets = (uint32_t)(CCSIDR_SETS(ccsidr));
00198     do {
00199       ways = (uint32_t)(CCSIDR_WAYS(ccsidr));
00200       do {
00201         SCB->DCCISW = (((sets << SCB_DCCISW_SET_Pos) & SCB_DCCISW_SET_Msk) |
00202                        ((ways << SCB_DCCISW_WAY_Pos) & SCB_DCCISW_WAY_Msk)  );
00203         #if defined ( __CC_ARM )
00204           __schedule_barrier();
00205         #endif
00206       } while (ways-- != 0U);
00207     } while(sets-- != 0U);
00208 
00209     __DSB();
00210     __ISB();
00211   #endif
00212 }
