00220 void SystemCoreClockUpdate(void)
00221 {
00222   uint32_t tmp = 0, pllvco = 0, pllp = 2, pllsource = 0, pllm = 2;
00223   
00224   /* Get SYSCLK source -------------------------------------------------------*/
00225   tmp = RCC->CFGR & RCC_CFGR_SWS;
00226 
00227   switch (tmp)
00228   {
00229     case 0x00:  /* HSI used as system clock source */
00230       SystemCoreClock = HSI_VALUE;
00231       break;
00232     case 0x04:  /* HSE used as system clock source */
00233       SystemCoreClock = HSE_VALUE;
00234       break;
00235     case 0x08:  /* PLL used as system clock source */
00236 
00237       /* PLL_VCO = (HSE_VALUE or HSI_VALUE / PLL_M) * PLL_N
00238          SYSCLK = PLL_VCO / PLL_P
00239          */    
00240       pllsource = (RCC->PLLCFGR & RCC_PLLCFGR_PLLSRC) >> 22;
00241       pllm = RCC->PLLCFGR & RCC_PLLCFGR_PLLM;
00242       
00243       if (pllsource != 0)
00244       {
00245         /* HSE used as PLL clock source */
00246         pllvco = (HSE_VALUE / pllm) * ((RCC->PLLCFGR & RCC_PLLCFGR_PLLN) >> 6);
00247       }
00248       else
00249       {
00250         /* HSI used as PLL clock source */
00251         pllvco = (HSI_VALUE / pllm) * ((RCC->PLLCFGR & RCC_PLLCFGR_PLLN) >> 6);
00252       }
00253 
00254       pllp = (((RCC->PLLCFGR & RCC_PLLCFGR_PLLP) >>16) + 1 ) *2;
00255       SystemCoreClock = pllvco/pllp;
00256       break;
00257     default:
00258       SystemCoreClock = HSI_VALUE;
00259       break;
00260   }
00261   /* Compute HCLK frequency --------------------------------------------------*/
00262   /* Get HCLK prescaler */
00263   tmp = AHBPrescTable[((RCC->CFGR & RCC_CFGR_HPRE) >> 4)];
00264   /* HCLK frequency */
00265   SystemCoreClock >>= tmp;
00266 }
